// hero-list.component.ts
import { Component, Inject, OnInit } from '@angular/core';
import { HeroService } from '../hero.service';

@Component({
  selector: 'app-hero-list',
  templateUrl: './hero-list.component.html',
  styleUrls: ['./hero-list.component.css'],
  providers: [HeroService],
})
export class HeroListComponent implements OnInit {
  heroes: any[] = [];

  constructor(@Inject(HeroService)private heroService: HeroService) {}

  ngOnInit(): void {
    this.loadHeroes();
  }

  loadHeroes(): void {
    this.heroService.getHeroes().subscribe((heroes: any[]) => {
      this.heroes = heroes;
    });
  }

  deleteHero(id: number): void {
    this.heroService.deleteHero(id).subscribe(() => {
      this.loadHeroes(); // Refresh the list after deletion
    });
  }
}
<!-- hero-list.component.html -->
<div>
  <h2>Hero List</h2>
  <table>
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Alias</th>
        <th>Brand</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let hero of heroes">
        <td>{{ hero.id }}</td>
        <td>{{ hero.name }}</td>
        <td>{{ hero.alias }}</td>
        <td>{{ hero.brand.name }}</td>
        <td>
          <button (click)="deleteHero(hero.id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
